// app.component.ts
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  form: FormGroup;

  constructor(private fb: FormBuilder) {
    this.form = this.fb.group({
      firstName: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(50), Validators.pattern('^[a-zA-Z]+$')]]
    });
  }

  ngOnInit() {
  }

  onSubmit() {
    if (this.form.valid) {
      console.log(this.form.value);
    }
  }

}
<h1>User Registration Form</h1>
<form [formGroup]="registrationForm" (ngSubmit)="onSubmit()">
      <div>
        <label for="inputFirstname">First Name</label>
        <input
          type="text"
          id="inputFirstname"
          formControlName="firstName"
          required
          minlength="3"
          maxlength="50"
          pattern="^[a-zA-Z ]+$"
        />
        <div id="errorFirstName" *ngIf="isFieldInvalid('firstName')">errorFirstName</div>
      </div>

      <div>
        <label for="gender">Gender</label>
        <label>
          <input type="checkbox" formControlName="gender" value="Male" />
          Male
        </label>
        <label>
          <input type="checkbox" formControlName="gender" value="Female" />
          Female
        </label>
        <div id="errorGender" *ngIf="isFieldInvalid('gender')">errorGender</div>
      </div>

      <div>
        <label for="country">Country of Origin</label>
        <input type="text" id="country" formControlName="country" required />
        <div id="errorCountry" *ngIf="isFieldInvalid('country')">errorCountry</div>
      </div>

      <div>
        <label for="city">City of Origin</label>
        <input type="text" id="city" formControlName="city" required />
        <div id="errorCity" *ngIf="isFieldInvalid('city')">errorCity</div>
      </div>

      <div>
        <label for="inputAge">age</label>
        <input type="number" id="inputAge" formControlName="age" [required]="isAgeRequired()" />
        <div id="errorAge" *ngIf="isFieldInvalid('age')">
          errorAge
        </div>
      </div>

      <button type="submit">Submit</button>
</form>
